import React, { Component } from "react";
import API from "../../utils/API";
import Container from "../../components/Container";
import SearchForm from "../../components/SearchForm";
// import SearchResults from "../../components/SearchResults";

class Search extends Component {
  state = {
    search: "",
    employees: [],
    filteredEmployees: [],
    error: ""
  };

  // When the component mounts, get a list of all employees and update this.state.employees
  componentDidMount() {
    API.getEmployeeList()
      .then(res => {
          this.setState({ employees: res.data.results })
      })
      .catch(err => console.log(err));
  }

  handleInputChange = event => {
    this.setState({ search: event.target.value });
  };

  handleFormSubmit = event => {
    event.preventDefault();
    const searchParam = this.state.search;

    let filteredList = [];

    filteredList = this.state.employees.filter(employee => employee.name.first === searchParam || employee.name.last === searchParam);

    this.setState({employees: filteredList})


    // const employeeList = this.state.employees;

    // employeeList.map(employee => {
      // const newList = employee.filter(employee => employee.name.first.includes(searchParam))
    // })

    // const newList = employeeList.filter(employee => employee.name.first.includes(searchParam))
    // filteredList.push(newList);
    // filter(employee => employee.name.first.includes(searchParam)));
    // this.setState({ filteredEmployees: filteredList });
    console.log(filteredList);
  }

  render() {
    return (
      <div>
        <SearchForm
            handleFormSubmit={this.handleFormSubmit}
            handleInputChange={this.handleInputChange}
            employees={this.state.employees}
          />
        <Container style={{ minHeight: "80%" }}>
            <table className="table">
              <thead>
                <tr>
                  <th scope="col">Picture</th>
                  <th scope="col">Name</th>
                  <th scope="col">Email</th>
                  <th scope="col">Phone Number</th>
                  <th scope="col">DOB</th>
                </tr>
              </thead>
              <tbody>
                {this.state.employees.map((employee) => {
                  return(<tr>
                    <td><img src={employee.picture.medium}></img></td>
                    <td>{employee.name.first} {employee.name.last}</td>
                    <td>{employee.email}</td>
                    <td>{employee.phone}</td>
                    <td>{employee.dob.date.split("T")[0]}</td>
                  </tr>)
                })} 
              </tbody>
            </table>
        </Container>
      </div>
    );
  }
}

export default Search;
