import React, { Component } from "react";
import API from "../../utils/API";
import Container from "../../components/Container";
import SearchForm from "../../components/SearchForm";
import SearchResults from "../../components/SearchResults";
// import Alert from "../components/Alert";

class Search extends Component {
  state = {
    search: "",
    employees: [],
    results: [],
    error: ""
  };

  // getEmployees = () => {
  //   API.getEmployeeList()
  //   .then(response => {
  //       for(let i=0; i<100; i++) {
  //         employeesArray.push(response.data.results[i]);
  //       }
  //       console.log(employeesArray);
  //   })
  // }

  // When the component mounts, get a list of all available base breeds and update this.state.breeds
  componentDidMount() {
    API.getEmployeeList()
      .then(res => {
        for(let i=0; i<100; i++) {
          this.setState({ employees: res.data.results[i] })
        }
      })
      .catch(err => console.log(err));

      console.log(this.state.employees);
  }

  handleInputChange = event => {
    this.setState({ search: event.target.value });
  };

  handleFormSubmit = event => {
    event.preventDefault();
    API.getEmployee(this.state.search)
      .then(res => {
        if (res.data.status === "error") {
          throw new Error(res.data.message);
        }
        this.setState({ results: res.data.message, error: "" });
      })
      .catch(err => this.setState({ error: err.message }));
  };


  render() {
    return (
      <div>
        <Container style={{ minHeight: "80%" }}>
          <h1 className="text-center">Search By Employee Name!</h1>
          <row>
            <table className="table">
              <thead>
                <th scope="col">Photo</th>
                <th scope="col">Name</th>
                <th scope="col">Email</th>
                <th scope="col">Phone Number</th>
                <th scope="col">DOB</th>
              </thead>
              <tbody>
                {/* {this.employeesArray.map((employee) => {
                  <tr>
                    <th scope="row">1</th>
                    <td>{this.employee.image}</td>
                    <td>{this.employee.name.first}</td>
                    <td>{this.employee.email}</td>
                    <td>{this.employee.phone}</td>
                    <td>{this.employee.birth}</td>
                  </tr>
                })}  */}
              </tbody>
            </table>
          </row>
          {/* <Alert
            type="danger"
            style={{ opacity: this.state.error ? 1 : 0, marginBottom: 10 }}
          >
            {this.state.error}
          </Alert> */}
          <SearchForm
            handleFormSubmit={this.handleFormSubmit}
            handleInputChange={this.handleInputChange}
            // breeds={this.state.breeds}
          />
          <SearchResults results={this.state.results} />
        </Container>
      </div>
    );
  }
}

export default Search;
